Zepto(function($){var t=$(".btn-scrolltop"),i=$(window),c=$(".sidebar").height()/2,s=!1;t.on("click",function(){$(this).animate({bottom:"50%",opacity:0.1},300,function(){$(this).hide();$(this).css("bottom","10%");$(this).css("opacity","1");s=!0;smoothScroll($(window),0,800);setTimeout(function(){s=!1},1000)})});i.scroll(function(){s||(i.scrollTop()>=c?t.show():t.hide())})});function smoothScroll(el,to,duration){if(duration<0){return}var difference=to-$(window).scrollTop();var perTick=difference/duration*10;this.scrollToTimerCache=setTimeout(function(){if(!isNaN(parseInt(perTick,10))){window.scrollTo(0,$(window).scrollTop()+perTick);smoothScroll(el,to,duration-10);s=!1}}.bind(this),10)};